import { useState } from "react";
import axios from "axios";
const Form = () => {
  const [values, setValues] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    sex: "",
    nameError: false,
  });

  console.log(values);
  const handleOnchange = (event) => {
    setValues({ ...values, [event.target.name]: event.target.value });
  };

  const handleChangeName = (event) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value.replace(/[^a-zA-Zก-ฮะ-์]/gi, ""),
      nameError: false,
    });
  };
  const handleChangeNumber = (event) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value.replace(/\D/g, ""),
      [event.target.name + "Error"]: false,
    });
  };

  const handleOnsubmit = (event) => {
    event.preventDefault();
    if (!values.firstName || !values.lastName) {
      setValues({ ...values, nameError: true });
      return;
    }
    if (!values.email.includes("@") || !values.email.includes(".")) {
      setValues({ ...values, emailError: true });
      return;
    }

    if (
      values.password.length < 8 ||
      values.password !== values.passwordRepeat
    ) {
      setValues({ ...values, passwordError: true });
      return;
    }
    const jsonData = {
      email: values.email,
      fname: values.fname,
      lname: values.lname,
      pid: values.pid,
      password: values.password,
      key: "@ud!t0r2ol9",
    };

    axios
      .post("http://pmcorporation.fortiddns.com:3000/register", jsonData)
      .then((res) => {
        if (res.data.message.includes(jsonData.pid)) {
          alert("ลงทะเบียนซ้ำ");
          window.location = "/";
        }
        if (res.data.status === "ok") {
          alert("ลงทะเบียนเสร็จสิ้น");
          window.location = "/";
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };
  return (
    <div onSubmit={handleOnsubmit}>
      <h1 style={{ textAlign: "center" }}>Demo Form</h1>
      <input
        name="firstName"
        onChange={handleChangeName}
        placeholder="First name"
      />
      <input
        name="lastName"
        onChange={handleChangeName}
        placeholder="Last name"
      />
      <input name="email" onChange={handleOnchange} placeholder="email" />
      <input name="phone" onChange={handleChangeNumber} placeholder="phone" />
      <div className="gender">
        <label htmlFor="male">Male</label>
        <input
          type="radio"
          name="sex"
          onChange={handleOnchange}
          id="male"
          value="male"
        />
        <label htmlFor="female">Female</label>
        <input
          type="radio"
          name="sex"
          onChange={handleOnchange}
          id="female"
          value="female"
        />
      </div>
      <button type="submit">Update</button>
    </div>
  );
};
export default Form;
