import { useState, useContext } from "react";

const Form = (props) => {
  const [values, setValues] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    sex: "",
    nameError: false,
    emailError: false,
    phoneError: false,
    sexError: false,
  });

  const handleOnchange = (event) => {
    setValues({ ...values, [event.target.name]: event.target.value });
  };

  const handleChangeName = (event) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value.replace(/[^a-zA-Zก-ฮะ-์]/gi, ""),
      nameError: false,
    });
  };
  const handleChangeNumber = (event) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value.replace(/\D/g, ""),
      [event.target.name + "Error"]: false,
    });
  };

  const handleOnsubmit = (event) => {
    event.preventDefault();

    if (!values.firstName || !values.lastName) {
      setValues({ ...values, nameError: true });
      return console.log("name");
    }
    if (!values.email.includes("@") || !values.email.includes(".")) {
      setValues({ ...values, emailError: true });
      return console.log("name");
    }
    if (!values.phone) {
      setValues({ ...values, phoneError: true });
      return console.log("name");
    }
    if (!values.sex) {
      setValues({ ...values, sexError: true });
      return console.log(console.log("name"));
    }
    const jsonData = {
      firstName: values.firstName,
      lastName: values.lastName,
      email: values.email,
      phone: values.phone,
      sex: values.sex,
    };
    props.onAppend(jsonData);

    axios
      .post("localhost:3333", jsonData)
      .then((res) => {
        if (res.data.message.includes(jsonData.pid)) {
          alert("ลงทะเบียนซ้ำ");
          window.location = "/";
        }
        if (res.data.status === "ok") {
          alert("ลงทะเบียนเสร็จสิ้น");
          window.location = "/";
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };
  return (
    <form onSubmit={handleOnsubmit}>
      <h1 style={{ textAlign: "center" }}>Demo Form</h1>
      <input
        name="firstName"
        value={values.firstName}
        type="text"
        onChange={handleChangeName}
        placeholder="First name"
      />
      <input
        name="lastName"
        value={values.lastName}
        type="text"
        onChange={handleChangeName}
        placeholder="Last name"
      />
      <input
        name="email"
        value={values.email}
        type="email"
        onChange={handleOnchange}
        placeholder="email"
      />
      <input
        name="phone"
        value={values.phone}
        type="text"
        onChange={handleChangeNumber}
        placeholder="phone"
      />
      <div className="gender">
        <label htmlFor="male">Male</label>
        <input
          type="radio"
          name="sex"
          onChange={handleOnchange}
          id="male"
          value="male"
          checked={values.sex === "male" ? true : false}
        />
        <label htmlFor="female">Female</label>
        <input
          type="radio"
          name="sex"
          onChange={handleOnchange}
          id="female"
          value="female"
          checked={values.sex === "female" ? true : false}
        />
      </div>
      <button type="submit">Update</button>
    </form>
  );
};
export default Form;
